INSERT INTO `products_description` (`products_id`, `language_id`, `products_name`, `products_description`, `products_url`, `products_viewed`, `product_meta_description`) VALUES
(30, 2, 'Автокресло JOY 8888', 'Автокресло JOY 8888 - универсальное. Расчитано на три возрастные категории /9-18кг, 15-25кг, 22-36кг/. Имеет пятиточечный ремень безопасности, спинка кресла регулируется в соответствии со спинкой автомобмльного сиденья и может откидываться на 180°, подголовник регулируется на 8 положений.', '', 91, ''),
(32, 2, 'Мягкая игрушка Лошадь', 'Мягкая игрушка Лошадь со звуком, 2 цвета. Длина 42 см.', '', 91, ''),
(34, 2, 'Мягкая игрушка Лошадь', 'Мягкая игрушка Лошадь, музыкальная, з цвета. Длина игрушки 40 см.', '', 34, ''),
(36, 2, 'Детский велосипед Super Bike 12"', 'Детский велосипед комплектуется двумя приставными колесиками, это делает безопасным катание для детей, которые только учатся ездить, с заниженной рамой ребенку будет намного легче садиться и слезать с велосипеда, надежные задние тормоза помогут быстро остановить велосипед. Имеет прочную стальную раму, на цепи есть защита от попадания в нее вещей и мусора, зеркало и звонок,установленные на руле, придают велосипеду элегантности, сзади имеется багажник, на котором можно что-то перевозить или катать друзей.\r\nЭто очень качественный и стильный велосипед, который станет надежным другом для вашего ребенка, катаясь на велосипеде малыш сможет развить физическую форму и координацию движений, а так же научится держать равновесие.\r\n\r\n\r\nХарактеристики:\r\n- диаметр колес 12 дюймов\r\n- дополнительные страховочные колесики\r\n- крылья\r\n- металлический багажник\r\n- звонок, зеркало заднего вида\r\n- защита цепи\r\n- задний ножной тормоз\r\n- высота руля и сиденья регулируемые\r\n- для детей от 3-х лет', '', 36, ''),
(39, 2, 'Детский велосипед PIXIE DUST 12"', 'Детский велосипед отлично подойдет девочке от 3-х лет. Велосипед снабжен двумя приставными колесиками, это делает безопасным катание для детей, которые только учатся ездить, с заниженной рамой ребенку будет намного легче садиться и слезать с велосипеда, надежные задние тормоза помогут быстро остановить велосипед. Имеет прочную стальную раму, на цепи есть защита от попадания в нее вещей и мусора, зеркало и звонок,установленные на руле, придают велосипеду элегантности, сзади имеется багажник, на котором можно что-то перевозить или катать друзей. Это очень качественный и стильный велосипед, который станет надежным другом для вашего ребенка, катаясь на велосипеде малыш сможет развить физическую форму и координацию движений, а так же научится держать равновесие.\r\nХарактеристики:\r\n- диаметр колес 12 дюймов\r\n- дополнительные страховочные колесики\r\n- крылья\r\n- металлический багажник\r\n- звонок, зеркало заднего вида\r\n- защита цепи\r\n- задний ножной тормоз\r\n- высота руля и сиденья регулируемые\r\n- для детей от 3-х лет', '', 16, ''),
(40, 2, 'Детский велосипед Принцесса София 12"', 'Детский велосипед представляет собой красивый велосипед с прочной стальной рамой и резиновыми колесами на спицах. Также велосипед снабжен двумя приставными колесиками, обеспечивающими дополнительную устойчивость, что дает возможность ребенку обучаться езде, и увереннее держать равновесие. Имеет удобное сидение, для большого комфорта и продления срока службы, высоту сиденья и руля можно регулировать. Эта модель оборудована пластмассовым декоративным щитком, зеркалом и звонком. Имеет задний ножной тормоз, обода окрашены водостойкой краской, спереди и сзади есть катафоты, имеется багажник, на цепи установлена защита. \r\n\r\nХарактеристики:\r\n- крепкая стальная рама\r\n- колеса с надувной камерой на спицах\r\n- диаметр колес 12 дюймов\r\n- дополнительные страховочные колесики\r\n- удобное сиденье\r\n- катафоты\r\n- декоративный щиток, нескользящие ручки\r\n- на руле есть звонок и зеркало заднего вида\r\n- крашенные обода\r\n- установлена защита цепи\r\n- задний ножной тормоз\r\n- высота руля и сиденья регулируемые\r\n- металлический багажник с зажимом\r\n- звездочки на спицах\r\n- для детей от 3-х лет', '', 20, ''),
(41, 2, 'Детский велосипед Ferrari 20"', 'Велосипед имеет удобную конструкцию, сиденье и руль регулируются по высоте, что позволяет ребенку кататься на велосипеде не один год. Велосипед оснащен двумя большими колесами и стальной подножкой, это дает возможность ребенку легко парковаться в удобном месте. Дизайн выполнен в фирменном стиле Ferrari. Имеет красивые и насыщенные цвета, которые обязательно привлекут внимание ребенка. Также имеются ручные и педальные тормоза, для лучшего обхвата на руле есть нескользящие ручки. Велосипед оборудован пластиковым рулевым щитком, колеса и рама окрашены водостойкой краской. Это очень удобный и практичный детский велосипед, который подарит ребенку массу эмоций и впечатлений.\r\nХарактеристика:\r\n- крепкая стальная рама\r\n- колеса с надувной камерой на спицах\r\n- диаметр колес 20 дюймов\r\n- удобное сиденье\r\n- катафоты\r\n- декоративный щиток, нескользящие ручки\r\n- на руле есть звонок и зеркало заднего вида\r\n- крашенные обода\r\n- высота руля и сиденья регулируется\r\n- установлена защита цепи\r\n- передний тормоз ручной: ободной механический\r\n- задний тормоз ножной: педальный\r\n- подножка', '', 2, ''),
(42, 2, 'Детский велосипед HUMMER 20"', 'Дизайн велосипеда выполнен в фирменном стиле HUMMER. Имеет красивые и насыщенные цвета, которые обязательно привлекут внимание ребенка. \r\nТакже имеются ручные и педальные тормоза, для лучшего обхвата на руле есть нескользящие ручки. Оборудован рулевой накладкой, колеса и рама окрашены водостойкой краской. Это очень удобный и практичный детский велосипед, который подарит ребенку массу эмоций и впечатлений. Велосипед имеет удобную конструкцию, сиденье и руль регулируются по высоте, что позволяет ребенку кататься на велосипеде не один год. Велосипед оснащен двумя большими колесами и стальной подножкой, это дает возможность ребенку легко парковаться в удобном месте. \r\nХарактеристика: \r\n- крепкая стальная рама \r\n- колеса с надувной камерой на спицах \r\n- диаметр колес 20 дюймов \r\n- удобное сиденье \r\n- катафоты \r\n- нескользящие ручки \r\n- на руле есть звонок и зеркало заднего вида \r\n- крашенные обода \r\n- высота руля и сиденья регулируется \r\n- установлена защита цепи \r\n- передний тормоз ручной: ободной механический \r\n- задний тормоз ножной: педальный \r\n- подножка', '', 5, ''),
(43, 2, 'Автобус 666-373-375-378', 'Радиоуправляемый автобус оснащен звуковыми и световыми эффектами и вращением на 360 градусов! Полное управление: движение вперед/назад/вправо/влево. Автобус работает от аккумулятора 4.8V, пульт от 2 батареек 1,5V АА. Для детей от 3-х лет', '', 93, ''),
(44, 2, 'Автобус 666-376', 'Радиоуправляемый автобус оснащен звуковыми и световыми эффектами и вращением на 360 градусов! Полное управление: движение вперед/назад/вправо/влево. Автобус работает от аккумулятора 4.8V, пульт от 2 батареек 1,5V АА. Для детей от 3-х лет', '', 18, ''),
